import React, {useState, useRef, FormEvent} from 'react';
import classes from './SearchBar.module.css';

const SearchBar: React.FC<{onSearch: (searchQuery: string, category: string) => void}> = (props) => {

  const searchText = useRef<HTMLInputElement>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [category, setCategory] = useState<string>('all');

  const handleCategoryChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setCategory(e.target.value);
  };

  const onSubmitHandler = (event: FormEvent) => {
    event.preventDefault();
    props.onSearch(searchText.current!.value, category);
    setSearchQuery('');

  }
  return <>
  <form className={classes.form} onSubmit={onSubmitHandler}>
  <div>
          <input
            type="radio"
            id="name"
            value="name"
            checked={category === 'name'}
            onChange={handleCategoryChange}
          />
          <label htmlFor="name">Name</label>
        </div>
        <div>
          <input
            type="radio"
            id="category"
            value="category"
            checked={category === 'category'}
            onChange={handleCategoryChange}
          />
          <label htmlFor="category">Category</label>
        </div>
        <div>
          <input
            type="radio"
            id="group"
            value="group"
            checked={category === 'group'}
            onChange={handleCategoryChange}
          />
          <label htmlFor="group">Group</label>
        </div>

    <div className={classes.searchBar}>
      <input type="text" placeholder="Search" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} ref={searchText} />
      <button type='submit'>Search</button>
      </div>
      </form>
  </>
}

export default SearchBar;
